import React, { Component } from "react";
import Login from './Login';
import Controls from './Controls';





export default class Dashboard extends Component {
    constructor(){
        super();
        this.state = {
            username: '',
            password: '',
            auth: false
        }
    }

    handleUsernameChange(e){
        e.preventDefault();
    }

    Authenticated() {
        if(this.state.auth === true) {
            return <Controls />
        } else {
            return <Login handleUsernameChange={this.handleUsernameChange} />
        }
    }

  render() {
    return (
      <div>
        {this.Authenticated()}
      </div>
    )
  }
}
