import React, { Component } from 'react'

export default class Login extends Component {
  constructor(props){
    super(props);


  }

  render() {
    return (
      <div className='container mt-5 bg-light py-5 px-5'>
      <h5 className='text-dark mb-5'>Dashboard Login</h5>
        <form className='form' >
    <div className='form-group'>
        <label>Username:</label>
        <input className='form-control border ' type="text" name="username"/>
    </div>
    <div className='form-group'>
        <label>Password:</label>
        <input className='form-control border ' type="password" name="password"/>
    </div>
    <div className='form-group'>
        <input className='btn btn-primary mt-4' onClick={(e) => props.handle(e)} id="submit"   value="Log In"/>
    </div>
</form>
      </div>
    )
  }
}
