import React, { Component } from "react";
import Login from "./components/Login";
import Dashboard from './components/Dashboard';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import Navbar from './components/Navbar'
import Landing from './components/Landing'


//need navbar, footer, main page, cards

//Authenticate layer
const Authenticate = (props) => {

 const authenticated = props.auth;

 if (authenticated === true){
   return <Dashboard />
 } 
 else {
   return <Login />
 }

}




class App extends Component {

  constructor(){
    super();
    this.state = {
      username: "",
      password: "",
      authenticated: true
    }
  }

  handleAuth() {

  }

  render() {
    return (
      <Router>
        <div className="App" style={{overflowX: 'hidden'}}>
        <Navbar />
        <Route exact path="/" component={Landing} />
        <Route exact path="/dashboard" render={(props) => <Authenticate auth={this.state.authenticated}{...props} />} />
        <Route exact path="/login" render={(props) => <Authenticate auth={this.state.authenticated} handleAuth={this.handleAuth}{...props} />} />
        </div>
      </Router>
    );
  }
}

export default App;
